    public Level() {

    }

    public Level(String level) {

        if (level != null && level != "") {

            JFrame frame = new JFrame("Grid BagLayout");
            frame.setSize(628, 417);
            frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
            frame.setResizable(false);
            frame.setUndecorated(true);

            JLabel background = new JLabel(new ImageIcon(getClass().getResource("/img/IMG_1885.JPG")));
            background.setLayout(null);
            background.setBounds(0, 0, 1000, 1550);

            JLabel close = new JLabel("X");
            close.setFont(new java.awt.Font("Ravie", 1, 15));
            close.setForeground(Color.WHITE);
            close.setBounds(600, 10, 30, 30);
            close.setCursor(new java.awt.Cursor(java.awt.Cursor.HAND_CURSOR));
            close.addMouseListener(new java.awt.event.MouseAdapter() {
                public void mouseClicked(java.awt.event.MouseEvent evt) {
                    frame.dispose();
                }
            });
            background.add(close);

            JLabel brain = new JLabel(level);
            brain.setFont(new java.awt.Font("Ravie", 1, 30));
            brain.setForeground(new java.awt.Color(246, 255, 4));
            brain.setBounds(80, 40, 150, 30);
            background.add(brain);

            java.sql.Connection conn = Koneksi.getKoneksi();
            try {
                java.sql.Statement stmt = conn.createStatement();
                String SQL = "select * from tb_gambar WHERE level_gambar = '" + level + "'";
                java.sql.ResultSet res = stmt.executeQuery(SQL);
                int i = 1;
                while (res.next()) {

                    JLabel player = new JLabel("" + i, SwingConstants.CENTER);
                    player.setFont(new java.awt.Font("Kristen ITC", 1, 25));
                    player.setText("" + i);
                    player.setForeground(new java.awt.Color(10, 19, 133));
                    player.setBackground(Color.WHITE);
                    player.setBounds((i + 4) + i * 90, 85, 80, 100);
                    player.setOpaque(true); 
                    player.setBorder(BorderFactory.createLineBorder(Color.WHITE, 5, true));
                    background.add(player);

                    player.setCursor(new Cursor(Cursor.HAND_CURSOR));
                    int id = res.getInt("id_gambar");
                    player.addMouseListener(new MouseAdapter() {
                        public void mouseClicked(MouseEvent e) {
//                             Jalankan fungsi dispose() saat label diklik
                            Gameplay game = new Gameplay(id,3);
                            game.showGui();
                            frame.dispose();
                        }
                    });

                    i++;
                }
            } catch (Exception e) {
                System.out.println(e.getMessage());
            }

            frame.add(background);
            frame.setLocationRelativeTo(null);
            frame.setVisible(true);
//            }
        }
    }

